import * as React from 'react';

import style from './button.st.css';

interface ButtonProps {
    label: string;
}

export class Button extends React.Component<ButtonProps> {
    render() {
        return (
            <div {...style('root', {}, this.props)}>
                <span className={style.label}>{this.props.label}</span>
            </div>    
        );
    }
}

src.button.st.css:
.root {
  position: relative;
  bottom: 0;
  right: 0;
  border: none;
  border-radius: 0;
  cursor: pointer;
  user-select: none;
  box-sizing: border-box;
}

.label {}

src.index.st.css:
:import {
    -st-from: "./button.st.css";
    -st-default: Button;
}
:global(*){
    box-sizing: border-box;
}
.root {
    padding: 2rem;
}

Button {
    max-width:300px;
    margin: 0 auto;
    margin-bottom:2em;
}

Button::label {
    text-align: center;
    font-family: "Arial";
    font-weight: 100;
    font-size: 1rem;
    line-height: 4rem;
    display: block;
    border: none;
    color: white;
    text-decoration: none;
}

.triple-layer {
    -st-extends: Button;
    background: white;
    background: linear-gradient(to right, #3F5EFB, #FC466B);
    border-radius: 100px;
    box-shadow: .5rem .5rem .5rem rgba(0,0,0,.5), -.5rem -.5rem rgba(255,0,0,.5);
    transition: all 75ms ease-in;
}

.triple-layer::label {
    text-transform: uppercase;
}

.triple-layer:hover {
    box-shadow: 1rem 1rem 1rem rgba(0,0,0,.5), -1rem -1rem rgba(255,0,0,.5);
} 

.notes-button {
    -st-extends: Button;
    border: 2px solid #000;
    background: #fff;
    transition: all 125ms ease-in-out;
    position: relative;
    top: -10px;
    left: -10px;
}

.notes-button::label {
    transition: all 125ms ease-in-out;
    position: relative;
    border: 2px solid black;
    color: black;
    background: white;
    top: 10px;
    left: 10px;
    width: 100%;
    display: block;
}

.notes-button:hover {
    border: 4px double rgba(0,0,0,.5);
    box-shadow: 0 0 1rem rgba(0,0,0,.5);
    background-color: rgba(54,243,32,.5);
    transform: rotate(-2deg);
}

.notes-button:hover::label {
    border: 4px double rgba(0,0,0,.2);
    box-shadow: 0 0 1rem rgba(0,0,0,.5);
    transform: rotate(4deg);
}

.gradient-animation {
    -st-extends: Button;
    border-radius: 100px;
    color: white;
    background: linear-gradient(-45deg, #EE7752, #E73C7E, #23A6D5, #23D5AB);
    background-size: 400% 400%;
    animation: Gradient 5s ease infinite;
}

@keyframes Gradient {
	0% {
		background-position: 0% 50%
	}
	50% {
		background-position: 100% 50%
	}
	100% {
		background-position: 0% 50%
	}
}

src.index.tsx:
import * as React from 'react';
import * as ReactDOM from 'react-dom';
import {Button} from './button';
import styles from './index.st.css';

if (document.body.querySelector('#container')) {
    document.body.querySelector('#container')!.remove();
}

const container = document.body.appendChild(document.createElement('div'));
container.setAttribute('id','container');

ReactDOM.render(
    <div className={styles.root}>
        <Button {...styles('triple-layer')} label="Triple Layer" />
        <Button {...styles('notes-button')} label="Notes Button" />
        <Button {...styles('gradient-animation')} label="Gradient Animation" />
    </div>
, container);
